  var regExp = 'ab';

  // 字面量 
  var reg_1 = /regExp/;
  var reg_1 = /^[a-zA-Z0-9]{3,8}$/;

  // 实例化
  var reg_2 = new RegExp(regExp+'1');
  var reg_2 = new RegExp(“[a-zA-Z0-9]{3,8}”);
