  var boy = {
    age: 18,
    address: { home: '北京' }
  }
  var _boy = Object.assign({}, boy);
  _boy.age = 20;
  _boy.address.home = "上海";
  console.log(boy.age, _boy.age);   // 18 20
  console.log(boy.address.home, _boy.address.home); // 上海 上海
